  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace sqlite;

    bool grew (false);

    // id
    //
    if (sk == statement_insert)
    {
      ::std::size_t const& v =
        o.id;

      bool is_null (false);
      sqlite::value_traits<
          ::std::size_t,
          sqlite::id_integer >::set_image (
        i.id_value,
        is_null,
        v);
      i.id_null = is_null;
    }

    // firstname
    //
    {
      ::std::string const& v =
        o.firstname;

      bool is_null (false);
      std::size_t cap (i.firstname_value.capacity ());
      sqlite::value_traits<
          ::std::string,
          sqlite::id_text >::set_image (
        i.firstname_value,
        i.firstname_size,
        is_null,
        v);
      i.firstname_null = is_null;
      grew = grew || (cap != i.firstname_value.capacity ());
    }

    // lastname
    //
    {
      ::std::string const& v =
        o.lastname;

      bool is_null (false);
      std::size_t cap (i.lastname_value.capacity ());
      sqlite::value_traits<
          ::std::string,
          sqlite::id_text >::set_image (
        i.lastname_value,
        i.lastname_size,
        is_null,
        v);
      i.lastname_null = is_null;
      grew = grew || (cap != i.lastname_value.capacity ());
    }

    // hire_date
    //
    {
      ::time_t const& v =
        o.hire_date;

      bool is_null (false);
      sqlite::value_traits<
          ::time_t,
          sqlite::id_integer >::set_image (
        i.hire_date_value,
        is_null,
        v);
      i.hire_date_null = is_null;
    }

    // base_rate
    //
    {
      double const& v =
        o.base_rate;

      bool is_null (true);
      sqlite::value_traits<
          double,
          sqlite::id_real >::set_image (
        i.base_rate_value,
        is_null,
        v);
      i.base_rate_null = is_null;
    }

    return grew;
  }

